aiw_quotes = aiw.filter.with_index{ |x,i| i.even? } 
aiw_characters = aiw.filter.with_index{ |x,i| i.odd? } 
aiw_characters_uniq = aiw_characters.uniq

aiw_characters_uniq.each { |x| Character.create(name: x, species:"", home: "") }

aiw_hashes = [] 

i = 0
while i < aiw_quotes.length do 
    character = Character.find_by(name: aiw_characters[i])

    aiw_hashes << {
        quote: aiw_quotes[i], 
        character_id: character.id, 
        book_id: 1}

    i = i + 1 
end 

aiw_hashes.each { |x| Quote.create(x)} 


